import styled from "styled-components";

export const PopupWrapper = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  height: 100vh;
  width: 100vw;
  background: #70707040;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 0 3.875rem;
`

export const PopupContent = styled.div`
  height: calc(100% - 12.5rem);
  width: calc(100% - 55rem);
  border-radius: 78px;
  background: white;
  position: relative;
`

export const PopupExitButton = styled.a`
  position: absolute;
  top: 2.50rem;
  left: 2.50rem;
  font-size: 1.6em;
  transition: all .5s;

  &:hover {
    color: #363535;
  }
`
