import styled from "styled-components";

export const TitleEditor = styled.input`
  font-family: Raleway;
  font-weight: bold;
  font-size: 2.54em;
  padding: 1rem;
  width: 100%;
  border: none;
  &:focus {
    outline: none;
  }
`

export const TextEditor = styled.div`
  min-height: 80%;
`
