import {link} from "./api";
import axios from "axios";

export async function listEditions(){
    const response = await axios.get(link+"editions/list/")
    return response.data
}

export async function addEdition(title){
    try{
        const token = sessionStorage.getItem('req_token')
        await axios.post(link+"editions/add/",{"title": title}, {headers: {"Authorization": "Token " + token}})
        return true
    } catch(e){
        return e
    }
}

export async function listArticles(edition){
    try{
        const token = sessionStorage.getItem('req_token')
        const response = await axios.get(link+"articles/list/"+edition, {headers: {"Authorization": "Token " + token}})
        return response.data
    } catch(e){
        return e
    }
}

export async function publishEdition(edition){
    try{
        const token = sessionStorage.getItem('req_token')
        const response = await axios.get(link+"editions/publish/"+edition, {headers: {"Authorization": "Token " + token}})
        return true
    } catch(e){
        return e
    }
}

export async function addArticle(redactor1, redactor2, tag, title, marker, edition){
    try{
        const token = sessionStorage.getItem('req_token')
        await axios.post(link+"editions/add/",{
