import axios from "axios";
import {link} from "./api";

export async function sendForgotMail(mail){
    try{
        await axios.get(link+"lost_password/"+mail)
        return true
    } catch(e) {
        return false
    }
}

export async function changePassword(token, password){
    try{
        await axios.post(link+"lost_password/"+token, {"password": password})
        return true
    } catch(e) {
        return false
    }
}
