import React, {useEffect, useRef, useState} from 'react';
import {
    DashboardContent,
    DashboardWrapper,
    EditionDetails, EditionMore, EditionQueue,
    EditionsContainer, EditionStatus,
    EditionsWrapper, EditionTitle
} from "../../styled/Dashboard.styled";
import DashboardNavigation from "../../components/DashboardNavigation";
import {AdminButton, AdminH1, AdminH2, ScrollButton} from "../../styled/Common.styled";
import {NewEditionPopup} from "../../components/NewEditionPopup";
import {Navigate} from "react-router-dom";
import {getUserData} from "../../api/handleLogin";

function DashboardEditions__chief() {

    const [userdata, setUserdata] = useState("");
    const [editionsdata, setEditionsdata] = useState("");

    useEffect(() => {
        getUserData().then(resp => setUserdata(resp))
    }, []);



    const newEditionPopupRef = useRef()

    function newEditionAppear() {
        newEditionPopupRef.current.style.display = "flex"
    }
        return (
            <DashboardWrapper>
                <NewEditionPopup ref={newEditionPopupRef} shown={false}/>
                <DashboardNavigation active={"home"}/>
                <DashboardContent>
                    <AdminH1>Bonjour, .</AdminH1>
                    <AdminH2>Dernières éditions du journal</AdminH2>
                    <EditionsWrapper>
                        <EditionsContainer>
                            <EditionDetails>
                                <EditionTitle>Édition de novembre 2022</EditionTitle>
                                <EditionStatus><i className="bi bi-clock-fill"></i> En cours de rédaction</EditionStatus>
                                <EditionQueue>2 articles en attente, 3 en correction, 1 terminé</EditionQueue>
                                <EditionMore href={""}>En voir +</EditionMore>
                            </EditionDetails>
                        </EditionsContainer>
                    </EditionsWrapper>
                    <AdminButton onClick={newEditionAppear}>Créer une nouvelle édition</AdminButton>
                </DashboardContent>
            </DashboardWrapper>
        );
}

export default DashboardEditions__chief;
