import React, {useEffect, useRef, useState} from 'react';
import {
    DashboardContent,
    DashboardWrapper,
    EditionDetails, EditionMore, EditionQueue,
    EditionsContainer, EditionStatus,
    EditionsWrapper, EditionTitle, ManagingArticlesContainer, ManagingEditionWrapper, ManagingRedactorsContainer
} from "../../styled/Dashboard.styled";
import DashboardNavigation from "../../components/DashboardNavigation";
import {AdminButton, AdminH1, AdminH2, ScrollButton} from "../../styled/Common.styled";
import {NewEditionPopup} from "../../components/NewEditionPopup";
import {NewTaskPopup} from "../../components/NewTaskPopup";
import {NewRedactorPopup} from "../../components/NewRedactorPopup";
import {getReportersList} from "../../api/userApiCalls";

function EditionManager() {
    const newRedactorPopupRef = useRef()

    function newRedactorAppear() {
        newRedactorPopupRef.current.style.display = "flex"
    }

    const [users, setUsers] = useState([
        {
            "id": 0,
            "username": "",
            "email": "",
            "first_name": "",
            "last_name": "",
            "groups": [0]
        }]);

    useEffect(() => {
        getReportersList().then(resp => {
            setUsers(resp)
        })
    }, []);

    return (
        <DashboardWrapper>
            <NewRedactorPopup ref={newRedactorPopupRef} shown={false}/>
            <DashboardNavigation active={"team"}/>
            <DashboardContent>
                <AdminH1>Bonjour, Mimoun.</AdminH1>
                <AdminH2>Gestion</AdminH2>
                    <AdminButton onClick={newRedactorAppear}>Ajouter un nouveau rédacteur</AdminButton>
                <AdminH2>Équipe actuelle</AdminH2>
                <ManagingRedactorsContainer>
                    <EditionDetails redactor={true}>
                        <EditionTitle>Mimoun Amar</EditionTitle>
                        <EditionStatus><i className="bi bi-pencil-fill"></i> Rédacteur</EditionStatus>
                        <EditionMore href={""}>Supprimer</EditionMore>
                    </EditionDetails>
