                        <EditionTitle>Mimoun Amar</EditionTitle>
                        <EditionStatus><i className="bi bi-pencil-fill"></i> Rédacteur</EditionStatus>
                        <EditionMore href={""}>Supprimer</EditionMore>
                    </EditionDetails>
                    <EditionDetails redactor={true}>
                        <EditionTitle>Mimoun Amar</EditionTitle>
                        <EditionStatus><i className="bi bi-pencil-fill"></i> Rédacteur</EditionStatus>
                        <EditionMore href={""}>Supprimer</EditionMore>
                    </EditionDetails>
                    <EditionDetails redactor={true}>
                        <EditionTitle>Mimoun Amar</EditionTitle>
                        <EditionStatus><i className="bi bi-pencil-fill"></i> Rédacteur</EditionStatus>
                        <EditionMore href={""}>Supprimer</EditionMore>
                    </EditionDetails>
                </ManagingRedactorsContainer>
            </DashboardContent>
        </DashboardWrapper>
    );
}

export default EditionManager;
