import axios from "axios";
import {link} from "./api";

export async function userApiCalls(username, password){
    try{
        await axios.post(link+"login/", {'username':username, 'password': password})
        return true
    } catch(e) {
        return false
    }
}

export async function generateToken(username, password) {
    try {
        const response = await axios.post(link + "token/", {'username': username, 'password': password})
        return response.data
    } catch (err) {
        return err;
    }
}

export async function validUser() {
    try {
        const token = sessionStorage.getItem('req_token')
        const response = await axios.get(link + "user/check/", {headers: {"Authorization": "Token " + token}})
        if (response.data.detail == "Invalid Token.") {
            return false
        } else {
            return true
        }
    } catch (err) {
        return false;
    }
}

export async function getUserData() {
    try {
        const token = sessionStorage.getItem('req_token')
        const response = await axios.get(link + "user/check/", {headers: {"Authorization": "Token " + token}})
        if (response.data.detail == "Invalid Token.") {
            return {"groups": [false]}
        } else {
            return response.data
        }
    } catch (err) {
        return {"groups": [false]};
    }
}

export async function getReportersList() {
    try {
        const token = sessionStorage.getItem('req_token')
        const response = await axios.get(link + "articles/add/", {headers: {"Authorization": "Token " + token}})
        return response.data
    }
    catch (err) {
        return err;
    }
}

export async function registerUser(firstName, lastName, email){
    try {
        const token = sessionStorage.getItem('req_token')
        const response = await axios.post(link + "user/handle/", {},{headers: {"Authorization": "Token " + token}})
        return true
    }
    catch (err) {
        return false;
    }
}
