import {validUser} from "../api/handleLogin";

export async function getRedirectStatus(auth_requirement)
{
    const logged_in = await validUser()
    if(!logged_in && auth_requirement){
        return true
    } else if(logged_in && !auth_requirement){
    return true
    } else {
        return false
    }
}
