import React, {useRef} from 'react';
import {
    DashboardContent,
    DashboardWrapper,
    EditionDetails, EditionMore, EditionQueue,
    EditionsContainer, EditionStatus,
    EditionsWrapper, EditionTitle, ManagingArticlesContainer, ManagingEditionWrapper
} from "../../styled/Dashboard.styled";
import DashboardNavigation from "../../components/DashboardNavigation";
import {AdminButton, AdminH1, AdminH2, ScrollButton} from "../../styled/Common.styled";
import {NewEditionPopup} from "../../components/NewEditionPopup";
import {NewTaskPopup} from "../../components/NewTaskPopup";

function EditionManager() {
    const newTaskPopupRef = useRef()

    function newEditionAppear() {
        newTaskPopupRef.current.style.display = "flex"
    }
    return (
        <DashboardWrapper>
            <NewTaskPopup ref={newTaskPopupRef} shown={false}/>
            <DashboardNavigation active={"team"}/>
            <DashboardContent>
                <AdminH1>Bonjour, Mimoun.</AdminH1>
                <AdminH2>Gestion</AdminH2>
                    <AdminButton>Ajouter un nouveau rédacteur</AdminButton>
                <AdminH2>Avancement</AdminH2>
                <div>
                    <EditionDetails>
                        <EditionTitle>Mimoun Amar</EditionTitle>
                        <EditionStatus><i className="bi bi-clock-fill"></i> En cours de rédaction</EditionStatus>
                        <EditionMore href={""}>En voir +</EditionMore>
                    </EditionDetails>
                </div>
            </DashboardContent>
        </DashboardWrapper>
    );
}

export default EditionManager;
