import React, {Component, forwardRef, useEffect, useRef} from 'react';
import {
    HeroImageComp,
    HeroLoginWrapper, LoginForgotPasswordLink,
    LoginForm,
    LoginFormWrapper,
    LoginWrapper,
    LogoLogin
} from "../styled/Login.styled";
import HeroImage from "../assets/press_login_hero.svg"
import PressLogo from "../assets/temp_mail_logo.png"
import {AdminButton, AdminH1, AdminInputIcon, AdminTextInput, AdminTextInputWrapper} from "../styled/Common.styled";
import "bootstrap-icons/font/bootstrap-icons.css"
import ForgotPasswordFormPopup from "../components/ForgotPasswordFormPopup";

function Login() {

    const forgotPopupRef = forwardRef()
    const forgotAppear = () => {
        console.log(forgotPopupRef.current)
    }

    function authenticate(e) {
        e.preventDefault()
    }

        return (
            <LoginWrapper>
                <ForgotPasswordFormPopup ref={forgotPopupRef} shown={false}/>
                <HeroLoginWrapper>
                    <HeroImageComp src={HeroImage}/>
                </HeroLoginWrapper>
               <LoginFormWrapper>
                   <LoginForm>
                       <LogoLogin>
                           <img src={PressLogo} alt={"Logo"}/>
                       </LogoLogin>
                       <AdminH1 centered={true}>Espace rédacteur</AdminH1>
                       <AdminTextInputWrapper>
                            <AdminTextInput type={"text"} placeholder={"Identifiant rédacteur"}/>
                           <AdminInputIcon className="bi bi-person-fill"/>
                       </AdminTextInputWrapper>
                       <AdminTextInputWrapper>
                           <AdminTextInput type={"password"} placeholder={"Mot de passe"}/>
                           <AdminInputIcon className="bi bi-key-fill"/>
                       </AdminTextInputWrapper>
                       <LoginForgotPasswordLink onClick={forgotAppear}>Mot de passe oublié</LoginForgotPasswordLink>
                       <AdminButton onClick={authenticate}>
                           Identification
                       </AdminButton>
                   </LoginForm>
               </LoginFormWrapper>
            </LoginWrapper>
        );
}

export default Login;
