import styled from "styled-components";
import FormBackground from "../assets/form.jpg";
import InputBackground from "../assets/form_txt.png";


export const CupideonFormWrapper = styled.div`
  background-image: url(${FormBackground});
  height: 100vh;
  width: 100vw;
  background-size: cover;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
`

export const CupideonFormTitleCrown = styled.div`
  height: 15vh;
`

export const CupideonFormTitleCrownFlower = styled.img`
  height: 15vh;
`

export const CupideonFormTitle = styled.h1`
  font-family: Wankstaberg Battles;
  font-size: 3.5em;
  color: #2C1B14;
`

export const CupideonFormInputContainer = styled.div`
  width: 100vw;
  height: 20vh;
  display: grid;
  grid-template-columns: 1fr 2fr 1fr;
`

export const CupideonFormHand = styled.img`
  width: 25vw;
`

export const CupideonFormTxtWrapper = styled.img`
  width: 30vw;
  text-align: center;
`

export const CupideonFormInputWrapper = styled.div`
  margin: 0;
  box-sizing: border-box;
  width: 50vw;
  text-align: center;
  position: relative;
`

export const CupideonFormInput = styled.input`
  margin: 0;
  box-sizing: border-box;
  position: absolute;
  left: 10vw;
  height: 90%;
  background: none;
  border: none;
  width: 30vw;
  text-align: center;
  text-overline: none;
  &:focus{
    text-overline: none;
  }
`
