import React from 'react';
import {PopupContent, PopupExitButton, PopupWrapper} from "../styled/Popup.styled";
import {
    AdminButton,
    AdminH1,
    AdminInputIcon,
    AdminP,
    AdminTextInput,
    AdminTextInputWrapper, ErrorContainer
} from "../styled/Common.styled";
import {sendForgotMail} from "../api/forgotPassword";
import * as PropTypes from "prop-types";

function GoodStatusContainer(props) {
    return null;
}

GoodStatusContainer.propTypes = {id: PropTypes.string};
export const ForgotPasswordFormPopup = React.forwardRef((props, ref) => {
    async function resetPasswordMail(e) {
        e.preventDefault()
        const query = document.querySelector("#mail-field").value
        const request = await sendForgotMail(query)
        if(request){

        } else {
            const errorWrapper = document.querySelector("#error-container")
            errorWrapper.innerText = "Aucun compte ne porte cet identifiant"
            errorWrapper.style.display="block"
        }
    }

    function forgotDisppear() {
        ref.current.style.display = "none"
    }
        return (
            <PopupWrapper shown={props.shown} ref={ref}>
                <PopupContent>
                    <PopupExitButton onClick={forgotDisppear}><i className="bi bi-x-lg"></i></PopupExitButton>
                    <AdminH1 centered={true} popup={true}>Mot de passe oublié</AdminH1>
                    <AdminTextInputWrapper popup={true}>
                        <AdminTextInput id={"mail-field"} type={"text"} placeholder={"Identifiant rédacteur"}/>
                        <AdminInputIcon className="bi bi-person-fill"/>
                    </AdminTextInputWrapper>
                    <ErrorContainer id={"error-container"}></ErrorContainer>
                    <GoodStatusContainer id={"good-container"}></GoodStatusContainer>
                    <AdminP>
                        Un e-mail de réinitialisation vous sera envoyé sur l’adresse renseignée lors de votre candidature à La Gazette de Léon. Veuillez vérifier votre boîte de réception ainsi que vos spams.
                    </AdminP>
                    <AdminButton onClick={resetPasswordMail}>
                        Réinitialiser votre mot de passe
                    </AdminButton>
                </PopupContent>
            </PopupWrapper>
        );
})
