import React, {Component, useCallback, useEffect, useRef, useState} from 'react';
import Quill from "quill"
import 'quill/dist/quill.snow.css'
import {json, useParams} from "react-router-dom";
import { ImageResize } from 'quill-image-resize-module-react';

function Editor() {
    const AUTOSAVE_INTERVAL = 2000
    const toolbarOptions = [[{ 'header': 1 }, { 'header': 2 }, { 'header': false }], ['image']]
    Quill.register('modules/imageResize', ImageResize);

    const {id} = useParams()
    const token = sessionStorage.getItem('req_token')
    const websocketLink = "ws://127.0.0.1:8000/ws/update/" + id + "/"+ token +"/"
    const [socket, setSocket] = useState();
    const [quill, setQuill] = useState();

    useEffect(() => {
        const s = new WebSocket(websocketLink)
        setSocket(s)
    }, []);

    useEffect(() => {
        if(socket == null || quill == null) return

        const handler = (delta, oldDelta, source) => {
            if(source !== 'user') return

            socket.send(JSON.stringify({'type' : 'update', 'changes': delta}))
        }
        quill.on('text-change', handler)

        return () => {
            quill.off('text-change', handler)
        }
    }, [socket, quill]);

    useEffect(() => {
        if(socket == null || quill == null) return

        socket.onmessage = (e) => {
            const data = JSON.parse(e.data)
            if(data.type == 'updated'){
                if(data.trigger.toString() == token.toString()) return
                quill.updateContents(data.message)
            } else if(data.type == 'connected'){
                const json = JSON.parse(data.message)
                quill.setContents(json)
                console.log(json)
                quill.enable()
            }
        }
    }, [socket, quill]);

    useEffect(() => {
        if(socket == null || quill == null) return
        const handler = () => {
            const data = quill.getContents()
            socket.send(JSON.stringify({'type' : 'save', 'changes': data}))
        }
        const interval = setInterval(handler, AUTOSAVE_INTERVAL)

        return () => {
            clearInterval(interval)
        }
    }, [socket, quill]);

    const wrapperRef = useCallback(
        (wrapper) => {
            if(wrapper == null) return

            wrapper.innerHTML = ""
            const editor = document.createElement('div')
            wrapper.append(editor)
            const q = new Quill(editor, {theme: "snow", modules: {toolbar: toolbarOptions, ImageResize:{parchment: Quill.import('parchment')}}})
            q.disable()
            q.setText("Loading...")
            setQuill(q)
        },
        [],
    );

        return (
            <div id={"container"} ref={wrapperRef}>

            </div>
        );
}

export default Editor;
