import Astrologie_Fev2023 from "../articles/astrologie-fevrier-2023.pdf"
import Sample from "../articles/sample.pdf"

export function findPDF(query){
    const articleMapper = [{
        title: "L'astrologie: un outil de découverte de soi.",
        id: "astrologie_outil_decouverte_de_soi_0223",
        file: Astrologie_Fev2023,
        edition: "FEV_23"
    }]
    const article = articleMapper.find(article => article.id === query);
    if(article != undefined){
        return article.file
    } else {
        return Sample
    }
}

export default findPDF()
