import React, {useEffect, useState} from "react";
import Login from "./Login";
import {getRedirectStatus, isAdmin} from "../helpers/redirection";

export function Dashboard() {

    const [redirect, setRedirect] = useState(false);
    const [admin, setAdmin] = useState(null);
    useEffect(() => {
        getRedirectStatus(true)
            .then(resp => {
                setRedirect(resp)
                console.log("STATUS")
            })
        isAdmin()
            .then(resp => {
                setAdmin(resp)
                console.log("RANK")
            }, [])
    });


    return(
        <>
            {redirect && <Navigate to={"/"}/>}
            {admin == true && <h1>Admin</h1>}
            {admin == false && <h1>Not Admin</h1>}
        </>
    )

}
