import React, {useEffect, useRef, useState} from 'react';
import {
    DashboardContent,
    DashboardWrapper,
    EditionDetails, EditionMore, EditionQueue,
    EditionsContainer, EditionStatus,
    EditionsWrapper, EditionTitle, ManagingArticlesContainer, ManagingEditionWrapper
} from "../../styled/Dashboard.styled";
import DashboardNavigation from "../../components/DashboardNavigation";
import {AdminButton, AdminH1, AdminH2, ScrollButton} from "../../styled/Common.styled";
import {NewEditionPopup} from "../../components/NewEditionPopup";
import {NewTaskPopup} from "../../components/NewTaskPopup";
import {useParams} from "react-router-dom";
import {listArticles} from "../../api/contentApiCall";

function EditionManager__chief() {
    const newTaskPopupRef = useRef()

    function newTaskAppear() {
        newTaskPopupRef.current.style.display = "flex"
    }
    const {id} = useParams()
    const [articles, setArticles] = useState([]);
    useEffect(() => {
        listArticles(id).then(resp => setArticles(resp)
        )
    }, []);

    return (
        <DashboardWrapper>
            <NewTaskPopup ref={newTaskPopupRef} shown={false}/>
            <DashboardNavigation active={"home"}/>
            <DashboardContent>
                <AdminH1>Édition de novembre 2022</AdminH1>
                <AdminH2>Gestion</AdminH2>
                <ManagingEditionWrapper>
                    <AdminButton onClick={newTaskAppear}>Attribuer une nouvelle tâche</AdminButton>
                    <AdminButton>Publier</AdminButton>
                </ManagingEditionWrapper>
                <AdminH2>Avancement</AdminH2>
                <ManagingArticlesContainer>
                        <EditionDetails editor={true}>
                            <EditionTitle>Le pont de Crimée endommagé</EditionTitle>
                            <EditionStatus><i className="bi bi-clock-fill"></i> En cours de rédaction</EditionStatus>
                            <EditionQueue>par Mimoun Amar</EditionQueue>
                            <EditionMore href={""}>En voir +</EditionMore>
                        </EditionDetails>
                    <EditionDetails editor={true}>
                        <EditionTitle>Les mots de la dissuasion nucléaire</EditionTitle>
                        <EditionStatus><i className="bi bi-pencil-fill"></i> En cours de correction</EditionStatus>
                        <EditionQueue>par Mimoun Amar</EditionQueue>
                        <EditionMore href={""}>En voir +</EditionMore>
                    </EditionDetails>
                    <EditionDetails editor={true}>
                        <EditionTitle>Olivier Dubois, depuis son extradition en France</EditionTitle>
                        <EditionStatus><i className="bi bi-check-lg"></i> Prêt à la publication</EditionStatus>
                        <EditionQueue>par Mimoun Amar</EditionQueue>
                        <EditionMore href={""}>En voir +</EditionMore>
                    </EditionDetails>


                </ManagingArticlesContainer>
            </DashboardContent>
        </DashboardWrapper>
    );
}

export default EditionManager__chief;
