import React from 'react';
import {PopupContent, PopupExitButton, PopupWrapper} from "../styled/Popup.styled";
import {
    AdminButton,
    AdminH1,
    AdminInputIcon,
    AdminP,
    AdminTextInput,
    AdminTextInputWrapper
} from "../styled/Common.styled";

function ForgotPasswordFormPopup() {
    function resetPassword(e) {
        e.preventDefault()
    }
        return (
            <PopupWrapper>
                <PopupContent>
                    <PopupExitButton><i className="bi bi-x-lg"></i></PopupExitButton>
                    <AdminH1 centered={true} popup={true}>Mot de passe oublié</AdminH1>
                    <AdminTextInputWrapper popup={true}>
                        <AdminTextInput type={"text"} placeholder={"Identifiant rédacteur"}/>
                        <AdminInputIcon className="bi bi-person-fill"/>
                    </AdminTextInputWrapper>
                    <AdminP>
                        Un e-mail de réinitialisation vous sera envoyé sur l’adresse renseignée lors de votre candidature à La Gazette de Léon. Veuillez vérifier votre boîte de réception ainsi que vos spams.
                    </AdminP>
                    <AdminButton onClick={resetPassword}>
                        Réinitialiser votre mot de passe
                    </AdminButton>
                </PopupContent>
            </PopupWrapper>
        );
}

export default ForgotPasswordFormPopup;
