import styled from "styled-components";

export const AdminH1 = styled.h1`
  font-family: Raleway;
  font-weight: bold;
  font-size: 2.54em;
  margin: 0;
  padding: 0;
  text-align: ${props => props.centered ? "center" : "left"};
`

export const AdminTextInput = styled.div`
  display: flex;
  background: #F4F5F5;
  border: 1px solid #707070;
  border-radius: 5px;
  height: 50px;
`
