import styled from "styled-components";

export const TitleEditor = styled.input`
  font-family: Raleway;
  font-weight: bold;
  font-size: 2.54em;
  margin-top: 4rem;
  margin-bottom: ${props => props.popup ? "3.3rem" : "0"};
  padding: 0;
  text-align: ${props => props.centered ? "center" : "left"};
`
